import styled from "styled-components";
import Tilt from "react-tilt";
export const LogoContainer = styled.div.attrs({
  className: `ma4 mt0`,
})``;

const Animation = styled(Tilt).attrs({
  className: "Tilt flex items-center br4 shadow-2 vh-20 w-30",
})``;

export const TiltContainer = styled(Animation)`
  background: rgb(176, 182, 237);
  background: -moz-radial-gradient(
    circle,
    rgba(176, 182, 237, 0.969625350140056) 54%,
    rgba(49, 137, 218, 0.9780287114845938) 100%
  );
  background: -webkit-radial-gradient(
    circle,
    rgba(176, 182, 237, 0.969625350140056) 54%,
    rgba(49, 137, 218, 0.9780287114845938) 100%
  );
  background: radial-gradient(
    circle,
    rgba(176, 182, 237, 0.969625350140056) 54%,
    rgba(49, 137, 218, 0.9780287114845938) 100%
  );
  filter: progid:DXImageTransform.Microsoft.gradient(startColorstr="#b0b6ed",endColorstr="#3189da",GradientType=1);
`;
