import styled from "styled-components";

const InputAndButtonWrapper = styled.div.attrs({
  className: "flex justify-center pa4 br3 shadow-5 w-30 center",
})``;

export const ButtonContainer = styled.button.attrs({
  className: "grow h2 f4 link  dib white bg-light-purple",
})``;

export const InputFormContainer = styled.input.attrs({
  className: "h2 shadow5",
})``;

export const InputAndButtonWrapperContainer = styled(InputAndButtonWrapper)`
  background: linear-gradient(#ffffff 50%, rgba(255, 255, 255, 0) 0) 0 0,
    radial-gradient(circle closest-side, #ffffff 53%, rgba(255, 255, 255, 0) 0)
      0 0,
    radial-gradient(circle closest-side, #ffffff 50%, rgba(255, 255, 255, 0) 0)
      55px 0 #48b;
  background-size: 25px 50px;
  background-repeat: repeat-x;
`;
