import styled from "styled-components";
import Tilt from "react-tilt";
export const LogoContainer = styled.div.attrs({
  className: `ma4 mt0`,
})``;

const Animation = styled(Tilt).attrs({
  className: "Tilt flex items-center br1 shadow-2 vh-20 w-30",
})``;

export const TiltContainer = styled(Animation)`
  background: rgb(34, 193, 195);
  background: -moz-linear-gradient(
    0deg,
    rgba(34, 193, 195, 1) 0%,
    rgba(253, 187, 45, 1) 100%
  );
  background: -webkit-linear-gradient(
    0deg,
    rgba(34, 193, 195, 1) 0%,
    rgba(253, 187, 45, 1) 100%
  );
  background: linear-gradient(
    0deg,
    rgba(34, 193, 195, 1) 0%,
    rgba(253, 187, 45, 1) 100%
  );
  filter: progid:DXImageTransform.Microsoft.gradient(startColorstr="#22c1c3",endColorstr="#fdbb2d",GradientType=1);
`;
